#' Writes maf summaries to an output tab-delimited text file.
#'
#' @param maf an MAF object generated by \code{read.maf}
#' @param basename basename for out file to be written.
#' @return NULL
#' @export

write.mafSummary = function(maf, basename = NULL){

  if(is.null(basename)){
    stop('Please provide a basename for output file.')
  }

  #write gene summary.
  write.table(x = paste(basename,'_geneSummary.txt', sep=''), maf@gene.summary, sep='\t', quote = F, row.names = F)
  #write sample summary.
  write.table(x = maf@variant.classification.summary, file = paste(basename,'_sampleSummary.txt', sep=''), sep='\t', quote = F, row.names = F)
  #write summary
  write.table(x = maf@summary,file = paste(basename,'_summary.txt', sep=''), sep='\t', quote = F, row.names = F)
  #write oncomatrix
  write.table(x = maf@oncoMatrix, file = paste(basename,'_oncomatrix.txt', sep=''), sep='\t', quote = F, row.names = T)
  #write oncomatrix numeric
  write.table(x = maf@numericMatrix, file = paste(basename,'_oncomatrixNumeric.txt', sep=''), sep='\t', quote = F, row.names = T)
}
